//mian1的 找工作
myApp.controller('main1ViewCtrls', function($scope,$state,$http,$rootScope,$log) {


    //初始化数据------------
    //是否允许加载
    $scope.moredata=true;
    //开始的页数
    var pages=0;
    //加载的条目
    var row=4;
    //招聘列表的数据
    $scope.zpList=[];

    //$scope.dataNull=false;
    //下拉刷新
    $scope.doRefresh = function() {

        //重新调用上拉加载
        $scope.goloadmore();
        $scope.$broadcast('scroll.refreshComplete');
    };

    //重新调用上拉加载
    $scope.goloadmore = function(){
        //把数据初始化
        pages= 0;
        $scope.zpList=[];
        $scope.moredata=true;
        $scope.dataNull=false;
        //调用上拉加载
        $scope.loadMore()
    }


    //上拉加载
    $scope.loadMore = function() {
        pages += 1;
         //console.log(pages+'----------------------------------------------');
        zhaopinList($scope,$http,pages,row,2);
        $scope.$broadcast('scroll.infiniteScrollComplete');
    };


    //搜索框
    $scope.searchs='';
    $scope.seachMain = function(){
        if($scope.searchs && $scope.searchs!=''){
            $scope.clearbtn=true;
        }else{
            $scope.clearbtn=false;
        }
    }
    //清除
    $scope.clearbtn=false;
    $scope.clearInput = function(){
        $scope.searchs='';
    }

    //默认区域选择的判断
    $scope.chosequyu=false;

    //所有条件  --  第一条默认选中
    $scope.dq_find1=true;

    //点击选择筛选条件
    $scope.findFround=function(index){
        //选区的背景
        $scope.xuanqu_box=true;

        if(index==1){
            $scope.dq_find1=true;

            $scope.gz_type=true;
            $scope.gz_didian=false;
            $scope.gz_yaoqiu=false;
            $scope.gz_hangye=false;
        }else if(index==2){
            $scope.dq_find2=true;

            $scope.gz_type=false;
            $scope.gz_didian=true;
            $scope.gz_yaoqiu=false;
            $scope.gz_hangye=false;

            $scope.sheng=true;
            $scope.shixian=false;


            if($scope.chosequyu){
                $scope.sheng=false;
                $scope.shixian=false;
                //默认区域选择的判断
                $scope.chosequyu=false;
            }else{
                //默认区域选择的判断
                $scope.chosequyu=true;
            }

        }else if(index==3){
            $scope.dq_find3=true;

            $scope.gz_type=false;
            $scope.gz_didian=false;
            $scope.gz_yaoqiu=true;
            $scope.gz_hangye=false;
        }else if(index==4){
            $scope.dq_find4=true;

            $scope.gz_type=false;
            $scope.gz_didian=false;
            $scope.gz_yaoqiu=false;
            $scope.gz_hangye=true;
        }else{

        }
    }

})

