//工作地点的
myApp.directive("gongAdre", function () {
    return {
        // scope: {}, //独立作用域
        restrict:"AE",
        templateUrl: 'view/main/main_list/selects/quyu.html',
        replace:true,
        controller: function($scope,$http,$filter) {  //暴露共用的属性和方法

            //获取全部省
            B.ready(function(){
                $http({
                    method: 'POST',
                    url: B.serverUrl+'/userProfile/userProfileAction!doNotNeedSessionAndSecurity_userProfileHandler.action',
                    data:{
                        jsonStr:'{"command": "245","content": {"pid": "3.1","level": "1"}}'
                    },
                    dataType: 'json'
                }).then(function successCallback(response) {
                    //所有城市
                    $scope.citydatas=response.data.obj;

                    //热门城市
                    $scope.city0=[];
                    //A-G
                    $scope.city1=[];
                    //H-M
                    $scope.city2=[];
                    //N-T
                    $scope.city3=[];
                    //U-Z
                    $scope.city4=[];

                    if ($scope.citydatas) {
                        for(var i in $scope.citydatas){
                            var text_af=makePy($scope.citydatas[i].text)[0].substring(0,1);
                            //console.log(text_af);

                            if(text_af=='A' || text_af=='B' || text_af=='C' || text_af=='D' || text_af=='E' || text_af=='F' || text_af=='G'){
                                $scope.city1.push($scope.citydatas[i])
                            }

                            if(text_af=='H' || text_af=='I' || text_af=='J' || text_af=='K' || text_af=='L' || text_af=='M'){
                                $scope.city2.push($scope.citydatas[i])
                            }

                            if(text_af=='N' || text_af=='O' || text_af=='P' || text_af=='R' || text_af=='S' || text_af=='T'){
                                $scope.city3.push($scope.citydatas[i])
                            }

                            if(text_af=='U' || text_af=='V' || text_af=='W' || text_af=='X' || text_af=='Y' || text_af=='Z' || text_af=='F'){
                                $scope.city4.push($scope.citydatas[i])
                            }
                        }
                    };

                    // alert('11:'+JSON.stringify(response));
                }, function errorCallback(response) {
                    //alert('22:'+JSON.stringify(response));
                });

            })


            //默认选则省显示
            $scope.sheng=true;
            //市与县隐藏
            $scope.shixian=false;

            //设置默认城市
            $scope.city=$scope.city0;

            //切换选择城市
            $scope.cityChange = function(i) {
                if(i==0){
                    $scope.city=$scope.city0;
                }else if(i==1){
                    $scope.city=$scope.city1;
                }else if(i==2){
                    $scope.city=$scope.city2;
                }else if(i==3){
                    $scope.city=$scope.city3;
                }else if(i==4){
                    $scope.city=$scope.city4;
                }
            };



            //点击省时
            $scope.cityclick = function(item) {
                // console.log(item);
                $scope.sheng=false;
                $scope.shixian=true;

                //把县制空
                $scope.city_xian=[];

                //根据 item.id 获取对应的市
                B.ready(function(){
                    $http({
                        method: 'POST',
                        url: B.serverUrl+'/userProfile/userProfileAction!doNotNeedSessionAndSecurity_userProfileHandler.action',
                        data:{
                            jsonStr:'{"command": "245","content": {"pid": "'+item.id+'","level": "2"}}'
                        },
                        dataType: 'json'
                    }).then(function successCallback(response) {
                        $scope.city_shi=response.data.obj;
                        // alert('11:'+JSON.stringify(response));
                    }, function errorCallback(response) {
                        //alert('22:'+JSON.stringify(response));
                    });
                })


                //当前选择的地点
                $scope.citydata={
                    text : item.text,
                };


            };

            //点击市
            $scope.changeCityShi=function(item){
                console.log(item);
                //根据 item.id 获取对应的县
                B.ready(function(){
                    $http({
                        method: 'POST',
                        url: B.serverUrl+'/userProfile/userProfileAction!doNotNeedSessionAndSecurity_userProfileHandler.action',
                        data:{
                            jsonStr:'{"command": "245","content": {"pid": "'+item.id+'","level": "2"}}'
                        },
                        dataType: 'json'
                    }).then(function successCallback(response) {
                        $scope.city_xian=response.data.obj;
                        // alert('11:'+JSON.stringify(response));
                    }, function errorCallback(response) {
                        //alert('22:'+JSON.stringify(response));
                    });
                })
                //当前选择的地点
                $scope.citydata={
                    text : item.text,
                };

            };

            //点击县
            $scope.changeCityXian=function(item){
                console.log(item);

                //当前选择的地点
                $scope.citydata={
                    text : item.text,
                };

                $scope.sheng=true;
                $scope.shixian=false;

                //选区的背景
                $scope.xuanqu_box=false;
                $scope.gz_didian=false;
                //重新调用上拉加载
                $scope.goloadmore();

            };

        },
        link: function(scope, element, attrs) {  //操作DOM
            //console.log(element.find(".dd_tabs"))
            //切换城市选择条件
            element.find(".dd_tabs").find('div').bind("click", function() {
                $(this).addClass('dq_cy');
                $(this).siblings().removeClass('dq_cy');
            });
            //点击城市
            $('.squyus').on('click',".spans",function(){
                $('.spans').removeClass('dq_city');
                $(this).addClass('dq_city');
            })

            //点击省
            $('.city_shi').on('click',".li_shi",function(){
                $(this).siblings().removeClass('dq_shi');
                $(this).addClass('dq_shi');
            })

            //点击县
            $('.city_xian').on('click',".li_xian",function(){
                $(this).siblings().removeClass('dq_xian');
                $(this).addClass('dq_xian');
            })

        }
    }
});

