myApp.directive("gongYaoQiu", function ($timeout) {
    return {
        // scope: {}, //独立作用域
        restrict:"AE",
        templateUrl: 'view/main/main_list/selects/hangye.html',
        replace:true,
        controller: function($scope) {  //暴露共用的属性和方法
            $scope.aa = "0,100";
            //点击确定
            $scope.yaoqiuBtn = function(){
                console.log(11)
                var workMn=$('.range-slider').val();
                var workJy=$('.dq_yqjy').html();
                var workXl=$('.dq_yqxl').html();
                alert(workMn+'--'+workJy+'--'+workXl);

                //选区的背景
                $scope.xuanqu_box=false;
                $scope.gz_didian=false;
                //重新调用上拉加载
                $scope.goloadmore();
            }

        },
        link: function(scope, element, attrs) {  //操作DOM
            //scope.aa = "0,100"
            scope.$watch('gz_yaoqiu', function() {
                if(scope.gz_yaoqiu){
                    $timeout(function(){
                        $('.range-slider').jRange({
                            from: 0,
                            to: 100,
                            step: 1,
                            scale: ['0k', '100k'],
                            format: '%s',
                            width: '100%',
                            showLabels: true,
                            isRange: true
                        });

                        $('.range-slider').jRange({
                            from: 0,
                            to: 100,
                            step: 1,
                            scale: ['0k', '100k'],
                            format: '%s',
                            width: 300,
                            showLabels: true,
                            isRange : true
                        });

                        //点击经验要求
                        $('.find_yaoqiu').on('click',".yqjy",function(){
                            $('.yqjy').removeClass('dq_yqjy');
                            $(this).addClass('dq_yqjy');
                            if($('.dq_yqxl').length==0){
                                $('.nums').html(2);
                            }else{
                                $('.nums').html(3);
                            }
                        })

                        //点击学历要求
                        $('.find_yaoqiu').on('click',".yqxl",function(){
                            $('.yqxl').removeClass('dq_yqxl');
                            $(this).addClass('dq_yqxl');
                            if($('.dq_yqjy').length==0){
                                $('.nums').html(2);
                            }else{
                                $('.nums').html(3);
                            }
                        })

                        //点击重置
                        $('.chongzhi').on('click',function(){
                            $('.yqjy').removeClass('dq_yqjy');
                            $('.yqxl').removeClass('dq_yqxl');
                        })

                    },100);
                }
            });

        }
    }
});

