function loadUser($scope,$http,$stateParams){
    console.log('{"command": "283","content": {"accountNum": "'+accountNum+'"}}')
    B.ready(function () {
        $http({
            method: 'POST',
            url: B.serverUrl+'/userProfile/userProfileAction!doNotNeedSessionAndSecurity_userProfileHandler.action',
            data:{
                jsonStr:'{"command": "283","content": {"accountNum": "'+accountNum+'"}}'
            },
            dataType: 'json'
        }).then(function successCallback(response) {
            console.log(response.data.obj);

            if(response.data.success){
                $scope.resumeObj=response.data.obj;

                //个人信息
                $scope.name = $scope.resumeObj.name;
                $scope.sex = $scope.resumeObj.sex;
                $scope.pic = $scope.resumeObj.headPic;

            }

        }, function errorCallback(response) {

        });

    })
}


//通过工厂模式创建自定义服务--------获取数据的服务-
myApp.factory('myFactoryHttp', function($http, $q) {
    return {
        // urls 为请求的地址 jsons 为请求的参数
        query : function(urls,jsons) {
            var deferred = $q.defer(); // 声明延后执行，表示要去监控后面的执行
            B.ready(function() {
                $http({
                    method: 'POST',
                    url: urls,
                    data: {
                        jsonStr: JSON.stringify(jsons)
                    },
                    dataType: 'json'
                }).then(function successCallback(response) {
                    deferred.resolve(response.data);  // 声明执行成功，即http请求数据成功，可以返回数据了
                }, function errorCallback(response) {
                    deferred.reject(response.data);   // 声明执行失败，即服务器返回错误
                });
            });
            return deferred.promise;   // 返回承诺，这里并不是最终数据，而是访问最终数据的API
        }
    };

});





